using System;namespace ProductionLineSimulator{    class Workplace    {        private int processedProductCount;        private int deferredThreshold;        private int deferredProductCount;        public Workplace(int deferredThreshold)        {            this.deferredThreshold = deferredThreshold;        }        public int ProcessedProductCount        {            get { return processedProductCount; }        }        public double AverageProcessingTime        {            get { return processedProductCount > 0 ? (double)deferredProductCount / processedProductCount : 0; }        }        public int Workload        {            get { return (int)Math.Round((double)deferredProductCount / deferredThreshold * 100); }        }        public void ProcessProduct(int count)        {                        processedProductCount += count;            if (processedProductCount > deferredThreshold)                deferredProductCount += count;        }    }}