using dynamic_simulation.Models;class ProductionLine{    public Workplace Workplace1 { get; }    public Workplace Workplace2 { get; }    public int ProcessTime { get; private set; }    public int ProducedProducts { get; private set; }    public int DelayedProducts { get; private set; }    public ProductionLine(int maxCapacity1, int maxCapacity2)    {        Workplace1 = new Workplace(maxCapacity1);        Workplace2 = new Workplace(maxCapacity2);        ProcessTime = 0;        ProducedProducts = 0;        DelayedProducts = 0;    }    public void Run(int numProducts)    {        for (int i = 0; i < numProducts; i++)        {            Product product = new Product(i + 1, 1);            bool loaded1 = Workplace1.LoadProduct(product);            bool loaded2 = false;            if (!loaded1)            {                loaded2 = Workplace2.LoadProduct(product);            }            if (!loaded1 && !loaded2)            {                DelayedProducts++;            }            ProcessTime += product.ProcessingTime;            ProducedProducts++;        }    }}