using System;using System.Collections.Generic;using System.Globalization;using System.Threading;using System.Threading.Tasks;using ProductionLineSimulator;namespace dynamic_simulation.ViewModels{    public class MainWindowViewModel : ViewModelBase    {        ProductionLine productionLine = new ProductionLine();        public MainWindowViewModel()        {            productionLine.LoadBuffer();            productionLine.Start();            // Ожидаем завершения работы потоков            Thread.Sleep(5000);            productionLine.PrintStatistics();            Console.ReadLine();        }    }}