using System;using System.Collections.Generic;using System.Globalization;namespace dynamic_simulation.ViewModels{    public class MainWindowViewModel : ViewModelBase    {        ProductionLine productionLine = new ProductionLine(8, 4);        public MainWindowViewModel()        {            productionLine.Run(3);            var a = ((double)productionLine.Workplace1.LoadedProducts / (double)productionLine.Workplace1.MaxCapacity) * 100;            var b = ((double)productionLine.Workplace2.LoadedProducts / (double)productionLine.Workplace2.MaxCapacity) * 100;                        Console.WriteLine("Загрузка рабочих мест: {0}%, {1}%", a,                b);            Console.WriteLine("Время обработки изделия на поточной линии: {0} ед. времени", productionLine.ProcessTime);            double delayedProbability = productionLine.DelayedProducts / (double)productionLine.ProducedProducts * 100;            Console.WriteLine("Вероятность попадания изделий в отложенное: " + delayedProbability + "%");        }    }}